@import "bootstrap";
@import "./masthead.css";
@import "./palette.css";

.rating {
    color: orange; /* Cambia il colore delle stelline */
}

.star {
    font-size: 20px; /* Dimensione delle stelline */
}

.my_alert-danger {
    background-color: rgba(246, 7, 7, 0.886);
}

.my-alert-success {
    color: white;
    background-color: green;
}

.error-message {
    min-height: 1rem;
    /* Riserva spazio per i messaggi di errore */
    display: flex;
    align-items: center;
    transition: all 0.3s ease-in-out; /* Aggiunge una transizione fluida */
    text-align: center;
}

.text-danger {
    font-size: 0.875rem;
}

.btn:hover:hover {
    background-color: var(--color-b);
    color: white;
    transition: 0.5s;
}

.altezza {
    height: 1000px;
}

.masthead-position {
    top: 75vh;
    /* background-color: red; */
    width: 100vw;
    display: flex;
    justify-content: center;
    align-items: center;
}

.text-shadow {
    text-shadow: 1px 2px 5px black;
}
.message {
    width: 30vw;
    margin: 0px;
}

@media screen and (max-width: 768px) {
    .message {
        width: 80vw;
    }
}

.current-image {
    display: inline-block;
    position: relative;
    margin-right: 10px;
}
.current-image img {
    display: block;
    max-width: 100px;
    max-height: 100px;
}
.current-image button {
    position: absolute;
    top: 0;
    right: 0;
}
