+import './bootstrap';
+import 'bootstrap';
+
+document.addEventListener('DOMContentLoaded', function () {
+    document.querySelectorAll('.prenota-btn').forEach(function (button) {
+        button.addEventListener('click', function () {
+            var routeId = this.getAttribute('data-route-id');
+
+            // Fetch route data
+            fetch(`/routes/${routeId}`)
+                .then(response => {
+                    // Verifica lo stato della risposta
+                    if (!response.ok) {
+                        throw new Error('Network response was not ok ' + response.statusText);
+                    }
+                    return response.json();
+                })
+                .then(data => {
+                    console.log('Success:', data); // Log dei dati
+                    // Update form fields
+                    document.getElementById('departureSelect').value = data.departure.id;
+                    document.getElementById('returnSelect').value = data.arrival.id;
+                })
+                .catch(error => {
+                    console.error('Error:', error); // Log dell'errore
+                    alert('Si è verificato un errore: ' + error.message);
+                });
+        });
+    });
+
+    // Enable/disable return date based on trip type
+    document.querySelectorAll('input[name="tripType"]').forEach(function (input) {
+        input.addEventListener('change', function () {
+            if (document.getElementById('solaAndata').checked) {
+                document.getElementById('dateReturn').disabled = true;
+            } else {
+                document.getElementById('dateReturn').disabled = false;
+            }
+        });
+    });
+});
